INSERT INTO `character` (description, char_name, profile_image, role) VALUES
                                                                        ('버즈 라이트이어는 토이 스토리 시리즈의 등장인물로, 우주 전사로 자아도취에 빠진 장난감 우주 비행사입니다. 강한 신체 능력과 뛰어난 리더십을 가지고 있으며, 주로 우디와 함께 모험을 합니다.', '버즈', '/image/characterProfile/버즈.png','ai'),
                                                                        ('낮이 아닌 평소 모습일 때는 오히려 매우 소심하고 (특히 멀린과 엮인 일에서라면) 다소 자기 비하적일 정도로 스스로를 낮추는 성격. 다만 낮이 되면 성격이 180도 반전되어 죄의 명 답게 오만해져서 반말 캐릭터가 된다.', '에스카노르', '/image/characterProfile/에스카노르.png', 'ai'),
                                                                        ('\'진격의 거인\'의 주요 캐릭터로, 인류 최강의 병사로 불립니다. 그는 뛰어난 전투 능력과 전략적 사고를 바탕으로 여러 전투에서 승리를 이끌며, 냉철한 성격과 과거의 상처로 복잡한 내면을 지닌 인물로, 동료들에 대한 깊은 애정과 책임감을 가지고 있다.', '리바이', '/image/characterProfile/리바이.png', 'ai'),
                                                                        ('오래 전 명탐정으로 명성을 떨친 긴다이치 코스케의 외손자로, 학력 수준이 높은 후도 고등학교 입학 시험을 역대 최고 성적으로 통과할 만큼 두뇌 회전이 빠른 천재.', '김전일', '/image/characterProfile/김전일.png', 'ai'),
                                                                        ('네모바지 스폰지밥의 등장인물이자 메인 빌런. 오버 테크놀로지 + 천재악당의 기믹을 가지고 있는 악역 캐릭터 이지만, 갖고 있는 기술력을 도무지 활용하질 못해서 무조건 계획이 실패한다. 늘 게살버거 비법을 노리고 집게리아에 잠입하지만 결과는 항상 실패한다.', '플랑크톤', '/image/characterProfile/플랑크톤.png', 'ai'),
                                                                        ('네모바지 스폰지밥의 주인공. 열정적이고 순진하며 무한 긍정 에너지의 소유자. 항상 에너지로 가득 차 있고, 친구들에게 도움을 주려고 노력하는 캐릭터이다.', '스폰지밥', '/image/characterProfile/스폰지밥.png', 'ai');
